import React, { useState } from 'react';
import {FormData} from './FormData'

export function Form() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [img, setImg] = useState()
  const [cardData, setCardData] = useState([])

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleUploadImg = (e) => {
    setImg(URL.createObjectURL(e.target.files[0]))
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    setCardData((prevCardData) => [...prevCardData, {name, email, img}])

  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name:</label>
        <input type="text" value={name} onChange={handleNameChange} />
      </div>
      <div>
        <label>Email:</label>
        <input type="email" value={email} onChange={handleEmailChange} />
      </div>
      <div class="mb-3">
  <label for="formFileSm" class="form-label">Upload your image</label>
  <input class="form-control form-control-sm" id="formFileSm" type="file" onChange={handleUploadImg}/>
</div>

      <button type="submit">Submit</button>
    </form>


        {name && email && img && (
      <FormData name={name} email={email} img={img} />
    )}
    </div>



  );
}
